// Parse HeyReach webhook payload and transform for Reply Handler agent
const body = $input.first().json.body;

// Validate required fields
if (!body.event_type || body.event_type !== 'message_received') {
  throw new Error('Invalid event type: expected message_received');
}

if (!body.lead?.linkedin_url || !body.message?.text) {
  throw new Error('Missing required fields: lead.linkedin_url or message.text');
}

// Transform to agent format
const agentPayload = {
  source: 'linkedin',
  reply: {
    lead_linkedin_url: body.lead.linkedin_url,
    lead_name: `${body.lead.first_name || ''} ${body.lead.last_name || ''}`.trim() || 'Unknown',
    company: body.lead.company || 'Unknown',
    title: body.lead.headline || '',
    campaign_id: body.campaign_id || null,
    conversation_id: body.message.conversation_id || null,
    body: body.message.text,
    received_at: body.message.timestamp || new Date().toISOString()
  },
  brain_id: body.brain_id || $env.DEFAULT_BRAIN_ID || 'default'
};

return [{ json: agentPayload }];

// Queue personalized follow-up for positive responses
const data = $input.first().json;

return [{
  json: {
    queued: true,
    action: 'personalized_followup',
    lead_linkedin_url: data.data?.lead_linkedin_url,
    lead_name: data.data?.lead_name,
    company: data.data?.company,
    suggested_response: data.data?.suggested_response,
    queue_time: new Date().toISOString(),
    follow_up_delay_hours: 24
  }
}];

