parameters.jsCode:
// Handle not interested or graceful close
const data = $input.first().json;

return [{
  json: {
    action: 'graceful_close',
    lead_linkedin_url: data.data?.lead_linkedin_url,
    lead_name: data.data?.lead_name,
    intent: data.data?.intent || 'not_interested',
    processed_at: new Date().toISOString(),
    close_sequence_triggered: true
  }
}];

// Aggregate final response
const originalPayload = $('Parse Message Event').first().json;
const agentResponse = $('Call Reply Handler Agent').first().json;

return [{
  json: {
    success: true,
    data: {
      source: 'linkedin',
      lead_linkedin_url: originalPayload.reply.lead_linkedin_url,
      lead_name: originalPayload.reply.lead_name,
      intent: agentResponse.data?.intent || 'unknown',
      processed_at: new Date().toISOString(),
      action_taken: true
    }
  }
}];
